                new Receipe() { idMeal= "52954",strMeal= "Hot and Sour Soup",strDrinkAlternate= null,strCategory= "Pork",strArea= "Chinese",strInstructions= "STEP 1 - MAKING THE SOUP\r\nIn a wok add chicken broth and wait for it to boil.\r\nNext add salt, sugar, sesame seed oil, white pepper, hot pepper sauce, vinegar and soy sauce and stir for few seconds.\r\nAdd Tofu, mushrooms, black wood ear mushrooms to the wok.\r\nTo thicken the sauce, whisk together 1 Tablespoon of cornstarch and 2 Tablespoon of water in a bowl and slowly add to your soup until it's the right thickness.\r\nNext add 1 egg slightly beaten with a knife or fork and add it to the soup and stir for 8 seconds\r\nServe the soup in a bowl and add the bbq pork and sliced green onions on top.",strMealThumb= "asserts/1529445893.jpg",strTags= "Soup",strYoutube= "https://www.youtube.com/watch?v=KgV9Zq3aSTo",strIngredient1= "Mushrooms",strIngredient2= "Wood Ear Mushrooms",strIngredient3= "Tofu",strIngredient4= "Pork",strIngredient5= "Chicken Stock",strIngredient6= "Salt",strIngredient7= "Sugar",strIngredient8= "Sesame Seed Oil",strIngredient9= "Pepper",strIngredient10= "Hotsauce",strIngredient11= "Vinegar",strIngredient12= "Soy Sauce",strIngredient13= "Cornstarch",strIngredient14= "Water",strIngredient15= "Spring Onions",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "1/3 cup",strMeasure2= "1/3 cup",strMeasure3= "2/3 Cup",strMeasure4= "1/2 cup ",strMeasure5= "2-1/2 cups",strMeasure6= "1/2 tsp",strMeasure7= "1/4 tsp",strMeasure8= "1 tsp ",strMeasure9= "1/4 tsp",strMeasure10= "1/2 tsp",strMeasure11= "1-½ cups",strMeasure12= "1 tsp ",strMeasure13= "1 tbs",strMeasure14= "2 tbs",strMeasure15= "1/4 cup",strMeasure16= "",strMeasure17= "",strMeasure18= "",strMeasure19= "",strMeasure20= "",strSource= "https://sueandgambo.com/pages/chinese-hot-and-sour-soup",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },

                new Receipe() { idMeal= "52972",strMeal= "Tunisian Lamb Soup",strDrinkAlternate= null,strCategory= "Lamb",strArea= "Tunisian",strInstructions= "Add the lamb to a casserole and cook over high heat. When browned, remove from the heat and set aside.\r\nKeep a tablespoon of fat in the casserole and discard the rest. Reduce to medium heat then add the garlic, onion and spinach and cook until the onion is translucent and the spinach wilted or about 5 minutes.\r\nReturn the lamb to the casserole with the onion-spinach mixture, add the tomato puree, cumin, harissa, chicken, chickpeas, lemon juice, salt and pepper in the pan. Simmer over low heat for about 20 minutes.\r\nAdd the pasta and cook for 15 minutes or until pasta is cooked.",strMealThumb= "asserts/t8mn9g1560460231.jpg",strTags= "Soup",strYoutube= "https://www.youtube.com/watch?v=w1qgTQmLRe4",strIngredient1= "Lamb Mince",strIngredient2= "Garlic",strIngredient3= "Onion",strIngredient4= "Spinach",strIngredient5= "Tomato Puree",strIngredient6= "Cumin",strIngredient7= "Chicken Stock",strIngredient8= "Harissa Spice",strIngredient9= "Chickpeas",strIngredient10= "Lemon Juice",strIngredient11= "Macaroni",strIngredient12= "Salt",strIngredient13= "Pepper",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "500g",strMeasure2= "2 cloves minced",strMeasure3= "1",strMeasure4= "300g",strMeasure5= "3 tbs",strMeasure6= "1 tbs",strMeasure7= "1 Litre",strMeasure8= "3 tsp",strMeasure9= "400g",strMeasure10= "1/2 ",strMeasure11= "150g",strMeasure12= "Pinch",strMeasure13= "Pinch",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "http://allrecipes.co.uk/recipe/16694/tunisian-lamb-soup.aspx",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "53047",strMeal= "Moroccan Carrot Soup",strDrinkAlternate= null,strCategory= "Vegetarian",strArea= "Moroccan",strInstructions= "Step 1\r\nPreheat oven to 180° C.\r\nStep 2\r\nCombine carrots, onion, garlic, cumin seeds, coriander seeds, salt and olive oil in a bowl and mix well. Transfer on a baking tray.\r\nStep 3\r\nPut the baking tray in preheated oven and roast for 10-12 minutes or till carrots soften. Remove from heat and cool.\r\nStep 4\r\nGrind the baked carrot mixture along with some water to make a smooth paste and strain in a bowl.\r\nStep 5\r\nHeat the carrot mixture in a non-stick pan. Add two cups of water and bring to a boil. Add garam masala powder and mix. Add salt and mix well.\r\nStep 6\r\nRemove from heat, add lemon juice and mix well.\r\nStep 7\r\nServe hot.",strMealThumb= "asserts/jcr46d1614763831.jpg",strTags= null,strYoutube= "https://www.youtube.com/watch?v=7tDWOpnIhok",strIngredient1= "Carrots",strIngredient2= "Onion",strIngredient3= "Garlic Clove",strIngredient4= "Cumin",strIngredient5= "Coriander",strIngredient6= "Olive Oil",strIngredient7= "Garam Masala",strIngredient8= "Lemon Juice",strIngredient9= "",strIngredient10= "",strIngredient11= "",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "6 chopped",strMeasure2= "1 sliced",strMeasure3= "4",strMeasure4= "1 tsp ",strMeasure5= "1/2 tsp",strMeasure6= "1 tbs",strMeasure7= "1/4 tsp",strMeasure8= "1 tsp ",strMeasure9= " ",strMeasure10= " ",strMeasure11= " ",strMeasure12= " ",strMeasure13= " ",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "https://www.sanjeevkapoor.com/Recipe/Moroccan-Carrot-Soup.html",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "53077",strMeal= "Cabbage Soup (Shchi)",strDrinkAlternate= null,strCategory= "Vegetarian",strArea= "Russian",strInstructions= "Add the butter to a large Dutch oven or other heavy-duty pot over medium heat. When the butter has melted, add the onion and sauté until translucent.\r\nAdd the cabbage, carrot, and celery. Sauté until the vegetables begin to soften, stirring frequently, about 3 minutes.\r\nAdd the bay leaf and vegetable stock and bring to a boil over high heat. Reduce the heat to low and simmer, covered, until the vegetables are crisp-tender, about 15 minutes.\r\nAdd the potatoes and bring it back to a boil over high heat. Reduce the heat to low and simmer, covered, until the potatoes are tender, about 10 minutes.\r\nAdd the tomatoes (or undrained canned tomatoes) and bring the soup back to a boil over high heat. Reduce the heat to low and simmer, uncovered, for 5 minutes. Season to taste with salt and pepper.\r\nemove and discard the bay leaf from the pot.\r\nServe topped with fresh sour cream and fresh dill.",strMealThumb= "asserts/60oc3k1699009846.jpg",strTags= "soup",strYoutube= "https://www.youtube.com/watch?v=lTjlbqGc_Y8",strIngredient1= "Unsalted Butter",strIngredient2= "Onion",strIngredient3= "Cabbage",strIngredient4= "Carrots",strIngredient5= "Celery",strIngredient6= "Bay Leaf",strIngredient7= "Vegetable Stock",strIngredient8= "Potatoes",strIngredient9= "Tomatoes",strIngredient10= "Sour Cream",strIngredient11= "Dill",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "3 tbs",strMeasure2= "1 large",strMeasure3= "1 medium",strMeasure4= "1",strMeasure5= "1",strMeasure6= "1",strMeasure7= "8 cups ",strMeasure8= "2 large",strMeasure9= "2 large",strMeasure10= "Garnish",strMeasure11= "Garnish",strMeasure12= " ",strMeasure13= " ",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "https://www.thespruceeats.com/traditional-russian-cabbage-soup-shchi-recipe-1135534",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "52842",strMeal= "Broccoli & Stilton soup",strDrinkAlternate= null,strCategory= "Starter",strArea= "British",strInstructions= "Heat the rapeseed oil in a large saucepan and then add the onions. Cook on a medium heat until soft. Add a splash of water if the onions start to catch.\r\n\r\nAdd the celery, leek, potato and a knob of butter. Stir until melted, then cover with a lid. Allow to sweat for 5 minutes. Remove the lid.\r\n\r\nPour in the stock and add any chunky bits of broccoli stalk. Cook for 10 – 15 minutes until all the vegetables are soft.\r\n\r\nAdd the rest of the broccoli and cook for a further 5 minutes. Carefully transfer to a blender and blitz until smooth. Stir in the stilton, allowing a few lumps to remain. Season with black pepper and serve.",strMealThumb= "asserts/tvvxpv1511191952.jpg",strTags= null,strYoutube= "https://www.youtube.com/watch?v=_HgVLpmNxTY",strIngredient1= "Rapeseed Oil",strIngredient2= "Onion",strIngredient3= "Celery",strIngredient4= "Leek",strIngredient5= "Potatoes",strIngredient6= "Butter",strIngredient7= "Vegetable Stock",strIngredient8= "Broccoli",strIngredient9= "Stilton Cheese",strIngredient10= "",strIngredient11= "",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "2 tblsp ",strMeasure2= "1 finely chopped ",strMeasure3= "1",strMeasure4= "1 sliced",strMeasure5= "1 medium",strMeasure6= "1 knob",strMeasure7= "1 litre hot",strMeasure8= "1 Head chopped",strMeasure9= "140g",strMeasure10= "",strMeasure11= "",strMeasure12= "",strMeasure13= "",strMeasure14= "",strMeasure15= "",strMeasure16= "",strMeasure17= "",strMeasure18= "",strMeasure19= "",strMeasure20= "",strSource= "https://www.bbcgoodfood.com/recipes/1940679/broccoli-and-stilton-soup",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "53078",strMeal= "Beetroot Soup (Borscht)",strDrinkAlternate= null,strCategory= "Vegetarian",strArea= "Ukrainian",strInstructions= "Chop the beetroot, add water and stock cube and cook for 15mins. Add the other ingredients and boil until soft. Finally add the beans and cook for 5mins. Serve in the soup pot.",strMealThumb= "asserts/zadvgb1699012544.jpg",strTags= "soup",strYoutube= "https://www.youtube.com/watch?v=6CXgPVw_-0g",strIngredient1= "Beetroot",strIngredient2= "Olive Oil",strIngredient3= "Chicken Stock Cube",strIngredient4= "Water",strIngredient5= "Potatoes",strIngredient6= "Cannellini Beans",strIngredient7= "Dill",strIngredient8= "",strIngredient9= "",strIngredient10= "",strIngredient11= "",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "3",strMeasure2= "4 tbs",strMeasure3= "1",strMeasure4= "6 cups ",strMeasure5= "3",strMeasure6= "1 can ",strMeasure7= "Garnish",strMeasure8= " ",strMeasure9= " ",strMeasure10= " ",strMeasure11= " ",strMeasure12= " ",strMeasure13= " ",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "https://natashaskitchen.com/classic-russian-borscht-recipe/",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "52981",strMeal= "Snert (Dutch Split Pea Soup)",strDrinkAlternate= null,strCategory= "Side",strArea= "Dutch",strInstructions= "Gather the ingredients.\r\n\r\nIn a large soup pot, bring water, split peas, pork belly or bacon, pork chop, and bouillon cube to a boil. Reduce the heat to a simmer, cover and let cook for 45 minutes, stirring occasionally and skimming off any foam that rises to the top. \r\n\r\nRemove the pork chop, debone, and thinly slice the meat. Set aside.\r\n\r\nAdd the celery, carrots, potato, onion, leek, and celeriac to the soup. Return to the boil, reduce the heat to a simmer and let cook, uncovered, for another 30 minutes, adding a little extra water if the ingredients start to stick to the bottom of the pot.\r\n\r\nAdd the smoked sausage for the last 15 minutes of cooking time. When the vegetables are tender, remove the bacon and smoked sausage, slice thinly and set aside.\r\n\r\nIf you prefer a smooth consistency, purée the soup with a stick blender. Season to taste with salt and pepper. Add the meat back to the soup, setting some slices of rookworst aside.\r\n\r\nServe in heated bowls or soup plates, garnished with slices of rookworst and chopped celery leaf.\r\n\r\nEnjoy!",strMealThumb= "asserts/9ptx0a1565090843.jpg",strTags= "Soup,Cake",strYoutube= "https://www.youtube.com/watch?v=5YV0sJLtLt8",strIngredient1= "Water",strIngredient2= "Peas",strIngredient3= "Pork",strIngredient4= "Vegetable Stock Cube",strIngredient5= "Celery",strIngredient6= "Carrots",strIngredient7= "Potatoes",strIngredient8= "Onion",strIngredient9= "Leek",strIngredient10= "Celeriac",strIngredient11= "Sausages",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "2L",strMeasure2= "300g",strMeasure3= "100g ",strMeasure4= "1",strMeasure5= "2",strMeasure6= "2",strMeasure7= "1 large",strMeasure8= "1 small",strMeasure9= "1 small",strMeasure10= "1 cup ",strMeasure11= "1 pound",strMeasure12= " ",strMeasure13= " ",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "https://www.thespruceeats.com/traditional-dutch-split-pea-soup-1129011",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "53059",strMeal= "Mushroom soup with buckwheat",strDrinkAlternate= null,strCategory= "Side",strArea= "Croatian",strInstructions= "Chop the onion and garlic, slice the mushrooms and wash the buckwheat. Heat the oil and lightly sauté the onion. Add the mushrooms and the garlic and continue to sauté. Add the salt, vegetable seasoning, buckwheat and the bay leaf and cover with water. Simmer gently and just before it is completely cooked, add pepper, sour cream mixed with flour, the chopped parsley and vinegar to taste.",strMealThumb= "asserts/1ngcbf1628770793.jpg",strTags= "Warming, Soup",strYoutube= "https://www.youtube.com/watch?v=M1ok7mG9-Qc",strIngredient1= "Mushrooms",strIngredient2= "Buckwheat",strIngredient3= "Vegetable Oil",strIngredient4= "Onion",strIngredient5= "Garlic",strIngredient6= "Bay Leaf",strIngredient7= "Vegetable Stock Cube",strIngredient8= "Sour Cream",strIngredient9= "White Wine Vinegar",strIngredient10= "Parsley",strIngredient11= "",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "150g",strMeasure2= "50g",strMeasure3= "4 tbs",strMeasure4= "40g",strMeasure5= "2 cloves",strMeasure6= "1",strMeasure7= "1tbsp",strMeasure8= "50 ml ",strMeasure9= "Dash",strMeasure10= "Top",strMeasure11= " ",strMeasure12= " ",strMeasure13= " ",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "https://www.visit-croatia.co.uk/croatian-cuisine/croatian-recipes/",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "53020",strMeal= "Rosół (Polish Chicken Soup)",strDrinkAlternate= null,strCategory= "Chicken",strArea= "Polish",strInstructions= "Add chicken to a large Dutch oven or stock pot \r\nCover with water\r\nBring to a boil and simmer for 2 to 2 1/2 hours, skimming any impurities off the top to insure a clear broth\r\nIf your pot is big enough, add the vegetables and spices for the last hour of the cooking time\r\nMy Dutch oven wasn’t big enough to hold everything, just the chicken and other bones filled the pot, so I cooked the meat/bones for the full cooking time, then removed them, and cooked the vegetables and spices separately\r\nStrain everything out of the broth\r\nBone the chicken, pulling the meat into large chunks\r\nSlice the carrots\r\nReturn the chicken and carrots to the broth\r\nCook noodles according to package instructions if you’re using them\r\nAdd noodles to bowl and then top with hot soup",strMealThumb= "asserts/lx1kkj1593349302.jpg",strTags= null,strYoutube= "https://www.youtube.com/watch?v=AU60fxyxXGw",strIngredient1= "Chicken Legs",strIngredient2= "Onions",strIngredient3= "Carrots",strIngredient4= "Leek",strIngredient5= "Celery",strIngredient6= "Cabbage",strIngredient7= "Cloves",strIngredient8= "Allspice",strIngredient9= "Bay Leaf",strIngredient10= "Parsley",strIngredient11= "Dill",strIngredient12= "Pepper",strIngredient13= "Salt",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "4 Chopped",strMeasure2= "2 large",strMeasure3= "5 chopped",strMeasure4= "1 chopped",strMeasure5= "1 small",strMeasure6= "1/4 ",strMeasure7= "1 whole",strMeasure8= "1tsp",strMeasure9= "2",strMeasure10= "4 sprigs",strMeasure11= "4 sprigs",strMeasure12= "1 tsp ",strMeasure13= "1 tbs",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "https://polishhousewife.com/rosol-polish-chicken-soup/",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null        },

                new Receipe() { idMeal= "53008",strMeal= "Stuffed Lamb Tomatoes",strDrinkAlternate= null,strCategory= "Lamb",strArea= "Greek",strInstructions= "Heat oven to 180C/160C fan/gas 4. Slice the tops off the tomatoes and reserve. Scoop out most of the pulp with a teaspoon, being careful not to break the skin. Finely chop the pulp, and keep any juices. Sprinkle the insides of the tomatoes with a little sugar to take away the acidity, then place them on a baking tray.\r\n\r\nHeat 2 tbsp olive oil in a large frying pan, add the onion and garlic, then gently cook for about 10 mins until soft but not coloured. Add the lamb, cinnamon and tomato purée, turn up the heat, then fry until the meat is browned. Add the tomato pulp and juice, the rice and the stock. Season generously. Bring to the boil, then simmer for 15 mins or until the rice is tender and the liquid has been absorbed. Set aside to cool a little, then stir in the herbs.\r\n\r\nStuff the tomatoes up to the brim, top tomatoes with their lids, drizzle with 2 tbsp more olive oil, sprinkle 3 tbsp water into the tray, then bake for 35 mins. Serve with salad and crusty bread, hot or cold.",strMealThumb= "asserts/u55lbp1585564013.jpg",strTags= null,strYoutube= "https://www.youtube.com/watch?v=KfJp-QfrCz4",strIngredient1= "Tomatoes",strIngredient2= "Sugar",strIngredient3= "Olive Oil",strIngredient4= "Onion",strIngredient5= "Garlic Clove",strIngredient6= "Lamb",strIngredient7= "Cinnamon",strIngredient8= "Tomato Puree",strIngredient9= "Rice",strIngredient10= "Chicken Stock",strIngredient11= "Dill",strIngredient12= "Chopped Parsley",strIngredient13= "Mint",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "4 large",strMeasure2= "Pinch",strMeasure3= "4 tbs",strMeasure4= "1 chopped",strMeasure5= "2 finely chopped",strMeasure6= "200g",strMeasure7= "1 tbs",strMeasure8= "2 tbs chopped",strMeasure9= "50g",strMeasure10= "100ml",strMeasure11= "4 tbs",strMeasure12= "2 tbs",strMeasure13= "1 tbs",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "https://www.bbcgoodfood.com/recipes/stuffed-tomatoes-lamb-mince-dill-rice",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "52782",strMeal= "Lamb tomato and sweet spices",strDrinkAlternate= null,strCategory= "Lamb",strArea= "Moroccan",strInstructions= "Use pickled vine leaves here, preserved in brine. Small delicate leaves are better than the large bristly ones but, if only large leaves are to hand, then trim them to roughly 12 by 12 cms so that you don't get too many layers of leaves around the filling. And remove any stalks. Drain the preserved leaves, immerse them in boiling water for 10 minutes and then leave to dry on a tea towel before use. \r\nBasmati rice with butter and pine nuts is an ideal accompaniment. Couscous is great, too. Serves four.\r\nFirst make the filling. Put all the ingredients, apart from the tomatoes, in a bowl. Cut the tomatoes in half, coarsely grate into the bowl and discard the skins. Add half a teaspoon of salt and some black pepper, and stir. Leave on the side, or in the fridge, for up to a day. Before using, gently squeeze with your hands and drain away any juices that come out.\r\nTo make the sauce, heat the oil in a medium pan. Add the ginger and garlic, cook for a minute or two, taking care not to burn them, then add the tomato, lemon juice and sugar. Season, and simmer for 20 minutes.\r\nWhile the sauce is bubbling away, prepare the vine leaves. Use any torn or broken leaves to line the base of a wide, heavy saucepan. Trim any leaves from the fennel, cut it vertically into 0.5cm-thick slices and spread over the base of the pan to cover completely.\r\nLay a prepared vine leaf (see intro) on a work surface, veiny side up. Put two teaspoons of filling at the base of the leaf in a 2cm-long by 1cm-wide strip. Fold the sides of the leaf over the filling, then roll it tightly from bottom to top, in a cigar shape. Place in the pan, seam down, and repeat with the remaining leaves, placing them tightly next to each other in lines or circles (in two layers if necessary).\r\nPour the sauce over the leaves (and, if needed, add water just to cover). Place a plate on top, to weigh the leaves down, then cover with a lid. Bring to a boil, reduce the heat and cook on a bare simmer for 70 minutes. Most of the liquid should evaporate. Remove from the heat, and leave to cool a little - they are best served warm. When serving, bring to the table in the pan - it looks great. Serve a few vine leaves and fennel slices with warm rice. Spoon the braising juices on top and garnish with coriander.",strMealThumb= "asserts/qtwtss1468572261.jpg",strTags= "",strYoutube= "https://www.youtube.com/watch?v=vaZb1MnFBgA",strIngredient1= "olive oil",strIngredient2= "ginger",strIngredient3= "garlic",strIngredient4= "tomatoes",strIngredient5= "lemon juice",strIngredient6= "caster sugar",strIngredient7= "vine leaves",strIngredient8= "fennel bulb",strIngredient9= "lamb mince",strIngredient10= "onion",strIngredient11= "potato",strIngredient12= "basmati rice",strIngredient13= "chopped parsley",strIngredient14= "coriander",strIngredient15= "lemon juice",strIngredient16= "garlic",strIngredient17= "clove",strIngredient18= "cinnamon",strIngredient19= "tomatoes",strIngredient20= "",strMeasure1= "2 tbsp",strMeasure2= "4cm piece finely chopped",strMeasure3= "2 cloves peeled and chopped",strMeasure4= "800g peeled and chopped ",strMeasure5= "2 tbsp",strMeasure6= "1 tsp",strMeasure7= "50",strMeasure8= "1 large",strMeasure9= "400g",strMeasure10= "1 medium",strMeasure11= "1 small peeled and coarsely grated",strMeasure12= "2 tbsp",strMeasure13= "2 tbsp",strMeasure14= "2 tbsp chopped",strMeasure15= "1 tbsp",strMeasure16= "2 cloves",strMeasure17= "½ tsp ground",strMeasure18= "½ tsp ground ",strMeasure19= "2 medium",strMeasure20= "",strSource= "http://www.ottolenghi.co.uk/recipes/meat/lamb-tomato-and-sweet-spices-shop",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "52959",strMeal= "Baked salmon with fennel & tomatoes",strDrinkAlternate= null,strCategory= "Seafood",strArea= "British",strInstructions= "Heat oven to 180C/fan 160C/gas 4. Trim the fronds from the fennel and set aside. Cut the fennel bulbs in half, then cut each half into 3 wedges. Cook in boiling salted water for 10 mins, then drain well. Chop the fennel fronds roughly, then mix with the parsley and lemon zest.\r\n\r\nSpread the drained fennel over a shallow ovenproof dish, then add the tomatoes. Drizzle with olive oil, then bake for 10 mins. Nestle the salmon among the veg, sprinkle with lemon juice, then bake 15 mins more until the fish is just cooked. Scatter over the parsley and serve.",strMealThumb= "asserts/1548772327.jpg",strTags= "Paleo,Keto,HighFat,Baking,LowCarbs",strYoutube= "https://www.youtube.com/watch?v=xvPR2Tfw5k0",strIngredient1= "Fennel",strIngredient2= "Parsley",strIngredient3= "Lemon",strIngredient4= "Cherry Tomatoes",strIngredient5= "Olive Oil",strIngredient6= "Salmon",strIngredient7= "Black Olives",strIngredient8= "",strIngredient9= "",strIngredient10= "",strIngredient11= "",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "2 medium",strMeasure2= "2 tbs chopped",strMeasure3= "Juice of 1",strMeasure4= "175g",strMeasure5= "1 tbs",strMeasure6= "350g",strMeasure7= "to serve",strMeasure8= "",strMeasure9= "",strMeasure10= "",strMeasure11= "",strMeasure12= "",strMeasure13= "",strMeasure14= "",strMeasure15= "",strMeasure16= "",strMeasure17= "",strMeasure18= "",strMeasure19= "",strMeasure20= "",strSource= "https://www.bbcgoodfood.com/recipes/7745/baked-salmon-with-fennel-and-tomatoes",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null        },

                new Receipe() { idMeal= "53040",strMeal= "Spring onion and prawn empanadas",strDrinkAlternate= null,strCategory= "Seafood",strArea= "Portuguese",strInstructions= "STEP 1\r\n\r\nTo make the dough, rub the butter into the flour and then add the egg white and half the yolk (keep the rest), vinegar, a pinch of salt and enough cold water to make a soft dough. Knead on a floured surface until smooth and then wrap and rest for 30 minutes.\r\n\r\nSTEP 2\r\n\r\nHeat the oven to 180c/fan 160c/gas 4. Trim the green ends of the spring onions and then finely slice the rest. Heat a little oil in a pan and fry them gently until soft but not browned. Add the chilli and garlic, stir and then add the prawns and cook until they are opaque. Season well. Scoop out the prawns and bubble the juices until they thicken, then add back the prawns.\r\n\r\nSTEP 3\r\n\r\nDivide the empanada dough into eight balls and roll out to thin circles on a floured surface. Put some filling on one half of the dough, sprinkle the feta on top and fold the other half over. Trim the edge and then fold and crimp the dough together so the empanada is tightly sealed, put it on an oiled baking sheet either on its side or sitting on its un-crimped edge like a cornish pasty. Repeat with the remaining dough and mixture. Mix the leftover egg yolk with a splash of water and brush the top of the empanadas.\r\n\r\nSTEP 4\r\n\r\nBake for 30 minutes or until golden and slightly crisp around the edges.",strMealThumb= "asserts/1c5oso1614347493.jpg",strTags= null,strYoutube= "https://www.youtube.com/watch?v=Q24haJU-1dI",strIngredient1= "Spring Onions",strIngredient2= "Olive Oil",strIngredient3= "Red Chilli",strIngredient4= "Garlic",strIngredient5= "Prawns",strIngredient6= "Feta",strIngredient7= "Butter",strIngredient8= "Plain Flour",strIngredient9= "Egg",strIngredient10= "White Wine Vinegar",strIngredient11= "",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "1  bunch",strMeasure2= "Dash",strMeasure3= "1 finely sliced",strMeasure4= "1 clove",strMeasure5= "350g",strMeasure6= "75g",strMeasure7= "15g",strMeasure8= "250g",strMeasure9= "1 Seperated",strMeasure10= "1/2 tsp",strMeasure11= " ",strMeasure12= " ",strMeasure13= " ",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "https://www.olivemagazine.com/recipes/fish-and-seafood/spring-onion-and-prawn-empanadas/",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "52996",strMeal= "French Onion Chicken with Roasted Carrots & Mashed Potatoes",strDrinkAlternate= null,strCategory= "Chicken",strArea= "American",strInstructions= "1\r\n\r\nPreheat oven to 425 degrees. Wash and dry all produce. Trim, peel, and cut carrots on a diagonal into ¼-inch-thick pieces. Dice potatoes into ½-inch pieces. Halve, peel, and thinly slice onion.\r\n\r\n2\r\n\r\nToss carrots on a baking sheet with a drizzle of oil, salt, and pepper. Roast until browned and tender, 15-20 minutes.\r\n\r\n3\r\n\r\nMeanwhile, place potatoes in a medium pot with enough salted water to cover by 2 inches. Bring to a boil and cook until tender, 12-15 minutes. Drain and return potatoes to pot; cover to keep warm.\r\n\r\n4\r\n\r\nWhile potatoes cook, heat a drizzle of oil in a large pan over medium-high heat. Add onion and cook, stirring occasionally, until lightly browned and softened, 8-10 minutes. Sprinkle with 1 tsp sugar (2 tsp for 4 servings). Stir in stock concentrate and 2 TBSP water (¼ cup for 4); season with salt and pepper. Cook until jammy, 2-3 minutes more. Turn off heat; transfer to a small bowl. Wash out pan.\r\n\r\n5\r\n\r\nPat chicken dry with paper towels; season all over with salt and pepper. Heat a drizzle of oil in pan used for onion over medium-high heat. Add chicken and cook until browned and cooked through, 5-6 minutes per side. In the last 1-2 minutes of cooking, top with caramelized onion and cheese. Cover pan until cheese melts. (If your pan doesn’t have a lid, cover with a baking sheet!)\r\n\r\n6\r\n\r\nHeat pot with drained potatoes over low heat; mash with sour cream, 2 TBSP butter (4 TBSP for 4 servings), salt, pepper, and a splash of water (or milk, for extra richness) until smooth. Divide chicken, roasted carrots, and mashed potatoes between plates.",strMealThumb= "asserts/b5ft861583188991.jpg",strTags= null,strYoutube= "",strIngredient1= "Chicken Breasts",strIngredient2= "Carrots",strIngredient3= "Small Potatoes",strIngredient4= "Onion",strIngredient5= "Beef Stock",strIngredient6= "Mozzarella",strIngredient7= "Sour Cream",strIngredient8= "Butter",strIngredient9= "Sugar",strIngredient10= "Vegetable Oil",strIngredient11= "Salt",strIngredient12= "Pepper",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "2",strMeasure2= "12 ounces",strMeasure3= "5",strMeasure4= "1",strMeasure5= "1",strMeasure6= "1 1/2 cup ",strMeasure7= "2 tbsp",strMeasure8= " ",strMeasure9= " ",strMeasure10= " ",strMeasure11= " ",strMeasure12= " ",strMeasure13= " ",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "52897",strMeal= "Carrot Cake",strDrinkAlternate= null,strCategory= "Dessert",strArea= "British",strInstructions= "For the carrot cake, preheat the oven to 160C/325F/Gas 3. Grease and line a 26cm/10in springform cake tin.\r\nMix all of the ingredients for the carrot cake, except the carrots and walnuts, together in a bowl until well combined. Stir in the carrots and walnuts.\r\nSpoon the mixture into the cake tin and bake for 1 hour 15 minutes, or until a skewer inserted into the middle comes out clean. Remove the cake from the oven and set aside to cool for 10 minutes, then carefully remove the cake from the tin and set aside to cool completely on a cooling rack.\r\nMeanwhile, for the icing, beat the cream cheese, caster sugar and butter together in a bowl until fluffy. Spread the icing over the top of the cake with a palette knife.",strMealThumb= "asserts/vrspxv1511722107.jpg",strTags= "Cake,Treat,Sweet",strYoutube= "https://www.youtube.com/watch?v=asjZ7iTrGKA",strIngredient1= "Vegetable Oil",strIngredient2= "Plain Flour",strIngredient3= "Bicarbonate Of Soda",strIngredient4= "Sugar",strIngredient5= "Eggs",strIngredient6= "Salt",strIngredient7= "Cinnamon",strIngredient8= "Carrots",strIngredient9= "Walnuts",strIngredient10= "Cream Cheese",strIngredient11= "Caster Sugar",strIngredient12= "Butter",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "450ml",strMeasure2= "400g",strMeasure3= "2 tsp",strMeasure4= "550ml",strMeasure5= "5",strMeasure6= "½ tsp",strMeasure7= "2 tsp",strMeasure8= "500g grated",strMeasure9= "150g",strMeasure10= "200g",strMeasure11= "150g",strMeasure12= "100g ",strMeasure13= "",strMeasure14= "",strMeasure15= "",strMeasure16= "",strMeasure17= "",strMeasure18= "",strMeasure19= "",strMeasure20= "",strSource= "https://www.bbc.co.uk/food/recipes/classic_carrot_cake_08513",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "53047",strMeal= "Moroccan Carrot Soup",strDrinkAlternate= null,strCategory= "Vegetarian",strArea= "Moroccan",strInstructions= "Step 1\r\nPreheat oven to 180° C.\r\nStep 2\r\nCombine carrots, onion, garlic, cumin seeds, coriander seeds, salt and olive oil in a bowl and mix well. Transfer on a baking tray.\r\nStep 3\r\nPut the baking tray in preheated oven and roast for 10-12 minutes or till carrots soften. Remove from heat and cool.\r\nStep 4\r\nGrind the baked carrot mixture along with some water to make a smooth paste and strain in a bowl.\r\nStep 5\r\nHeat the carrot mixture in a non-stick pan. Add two cups of water and bring to a boil. Add garam masala powder and mix. Add salt and mix well.\r\nStep 6\r\nRemove from heat, add lemon juice and mix well.\r\nStep 7\r\nServe hot.",strMealThumb= "asserts/jcr46d1614763831.jpg",strTags= null,strYoutube= "https://www.youtube.com/watch?v=7tDWOpnIhok",strIngredient1= "Carrots",strIngredient2= "Onion",strIngredient3= "Garlic Clove",strIngredient4= "Cumin",strIngredient5= "Coriander",strIngredient6= "Olive Oil",strIngredient7= "Garam Masala",strIngredient8= "Lemon Juice",strIngredient9= "",strIngredient10= "",strIngredient11= "",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "6 chopped",strMeasure2= "1 sliced",strMeasure3= "4",strMeasure4= "1 tsp ",strMeasure5= "1/2 tsp",strMeasure6= "1 tbs",strMeasure7= "1/4 tsp",strMeasure8= "1 tsp ",strMeasure9= " ",strMeasure10= " ",strMeasure11= " ",strMeasure12= " ",strMeasure13= " ",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "https://www.sanjeevkapoor.com/Recipe/Moroccan-Carrot-Soup.html",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "52996",strMeal= "French Onion Chicken with Roasted Carrots & Mashed Potatoes",strDrinkAlternate= null,strCategory= "Chicken",strArea= "American",strInstructions= "1\r\n\r\nPreheat oven to 425 degrees. Wash and dry all produce. Trim, peel, and cut carrots on a diagonal into ¼-inch-thick pieces. Dice potatoes into ½-inch pieces. Halve, peel, and thinly slice onion.\r\n\r\n2\r\n\r\nToss carrots on a baking sheet with a drizzle of oil, salt, and pepper. Roast until browned and tender, 15-20 minutes.\r\n\r\n3\r\n\r\nMeanwhile, place potatoes in a medium pot with enough salted water to cover by 2 inches. Bring to a boil and cook until tender, 12-15 minutes. Drain and return potatoes to pot; cover to keep warm.\r\n\r\n4\r\n\r\nWhile potatoes cook, heat a drizzle of oil in a large pan over medium-high heat. Add onion and cook, stirring occasionally, until lightly browned and softened, 8-10 minutes. Sprinkle with 1 tsp sugar (2 tsp for 4 servings). Stir in stock concentrate and 2 TBSP water (¼ cup for 4); season with salt and pepper. Cook until jammy, 2-3 minutes more. Turn off heat; transfer to a small bowl. Wash out pan.\r\n\r\n5\r\n\r\nPat chicken dry with paper towels; season all over with salt and pepper. Heat a drizzle of oil in pan used for onion over medium-high heat. Add chicken and cook until browned and cooked through, 5-6 minutes per side. In the last 1-2 minutes of cooking, top with caramelized onion and cheese. Cover pan until cheese melts. (If your pan doesn’t have a lid, cover with a baking sheet!)\r\n\r\n6\r\n\r\nHeat pot with drained potatoes over low heat; mash with sour cream, 2 TBSP butter (4 TBSP for 4 servings), salt, pepper, and a splash of water (or milk, for extra richness) until smooth. Divide chicken, roasted carrots, and mashed potatoes between plates.",strMealThumb= "asserts/b5ft861583188991.jpg",strTags= null,strYoutube= "",strIngredient1= "Chicken Breasts",strIngredient2= "Carrots",strIngredient3= "Small Potatoes",strIngredient4= "Onion",strIngredient5= "Beef Stock",strIngredient6= "Mozzarella",strIngredient7= "Sour Cream",strIngredient8= "Butter",strIngredient9= "Sugar",strIngredient10= "Vegetable Oil",strIngredient11= "Salt",strIngredient12= "Pepper",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "2",strMeasure2= "12 ounces",strMeasure3= "5",strMeasure4= "1",strMeasure5= "1",strMeasure6= "1 1/2 cup ",strMeasure7= "2 tbsp",strMeasure8= " ",strMeasure9= " ",strMeasure10= " ",strMeasure11= " ",strMeasure12= " ",strMeasure13= " ",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "52997",strMeal= "Beef Banh Mi Bowls with Sriracha Mayo, Carrot & Pickled Cucumber",strDrinkAlternate= null,strCategory= "Beef",strArea= "Vietnamese",strInstructions= "Add'l ingredients= mayonnaise, siracha\r\n\r\n1\r\n\r\nPlace rice in a fine-mesh sieve and rinse until water runs clear. Add to a small pot with 1 cup water (2 cups for 4 servings) and a pinch of salt. Bring to a boil, then cover and reduce heat to low. Cook until rice is tender, 15 minutes. Keep covered off heat for at least 10 minutes or until ready to serve.\r\n\r\n2\r\n\r\nMeanwhile, wash and dry all produce. Peel and finely chop garlic. Zest and quarter lime (for 4 servings, zest 1 lime and quarter both). Trim and halve cucumber lengthwise; thinly slice crosswise into half-moons. Halve, peel, and medium dice onion. Trim, peel, and grate carrot.\r\n\r\n3\r\n\r\nIn a medium bowl, combine cucumber, juice from half the lime, ¼ tsp sugar (½ tsp for 4 servings), and a pinch of salt. In a small bowl, combine mayonnaise, a pinch of garlic, a squeeze of lime juice, and as much sriracha as you’d like. Season with salt and pepper.\r\n\r\n4\r\n\r\nHeat a drizzle of oil in a large pan over medium-high heat. Add onion and cook, stirring, until softened, 4-5 minutes. Add beef, remaining garlic, and 2 tsp sugar (4 tsp for 4 servings). Cook, breaking up meat into pieces, until beef is browned and cooked through, 4-5 minutes. Stir in soy sauce. Turn off heat; taste and season with salt and pepper.\r\n\r\n5\r\n\r\nFluff rice with a fork; stir in lime zest and 1 TBSP butter. Divide rice between bowls. Arrange beef, grated carrot, and pickled cucumber on top. Top with a squeeze of lime juice. Drizzle with sriracha mayo.",strMealThumb= "asserts/z0ageb1583189517.jpg",strTags= null,strYoutube= "",strIngredient1= "Rice",strIngredient2= "Onion",strIngredient3= "Lime",strIngredient4= "Garlic Clove",strIngredient5= "Cucumber",strIngredient6= "Carrots",strIngredient7= "Ground Beef",strIngredient8= "Soy Sauce",strIngredient9= "",strIngredient10= "",strIngredient11= "",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "White",strMeasure2= "1",strMeasure3= "1",strMeasure4= "3",strMeasure5= "1",strMeasure6= "3 oz ",strMeasure7= "1 lb",strMeasure8= "2 oz ",strMeasure9= " ",strMeasure10= " ",strMeasure11= " ",strMeasure12= " ",strMeasure13= " ",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "52992",strMeal= "Soy-Glazed Meatloaves with Wasabi Mashed Potatoes & Roasted Carrots",strDrinkAlternate= null,strCategory= "Beef",strArea= "American",strInstructions= "1. Preheat oven to 425 degrees. Wash and dry all produce. Dice potatoes into 1/2-inch pieces. Trim, peel, and cut carrots on a diagonal into 1/2-inch-thick pieces. Trim and thinly slice scallions, separating whites from greens; finely chop whites. Peel and finely chop garlic.\r\n\r\n2. In a medium bowl, soak bread with 2 TBSP water (4 TBSP for 4 servings); break up with your hands until pasty. Stir in beef, sriracha, scallion whites, half the garlic, salt (we used 3/4 tsp kosher salt; 11/2 tsp for 4), and pepper. Form into two 1-inch-tall loaves (four loaves for 4). Place on one side of a baking sheet. Toss carrots on empty side of same sheet with a drizzle of oil, salt, and pepper. (For 4, spread meatloaves out across whole sheet and add carrots to a second sheet.) Bake for 20 minutes (we'll glaze the meatloaves then).\r\n\r\n3. Meanwhile, place potatoes in a medium pot with enough salted water to cover by 2 inches. Bring to a boil and cook until very\r\ntender, 12-15 minutes. Reserve 1/2 cup potato cooking liquid, then drain. While potatoes cook, in a small bowl, combine soy sauce, garlic powder, 1/4 cup ketchup (1/2 cup for 4 servings), and 2 tsp sugar (4 tsp for 4).\r\n\r\n4. Once meatloaves and carrots have baked 20 minutes, remove from oven. Spoon half the ketchup glaze over meatloaves (save\r\nthe rest for serving); return to oven until carrots are browned and tender, meatloaves are cooked through, and glaze is tacky, 4-5 minutes more.\r\n\r\n5. Meanwhile, melt 2 TBSP butter (4 TBSP for 4 servings) in pot used for potatoes over medium heat. Add remaining garlic and cook\r\nuntil fragrant, 30 seconds. Add potatoes and 1/4 tsp wasabi. Mash, adding splashes of reserved potato cooking liquid as necessary until smooth. Season with salt and pepper. (If you like things spicy, stir in more wasabi!)\r\n\r\n6. Divide meatloaves, mashed potatoes, and roasted carrots between plates. Sprinkle with scallion greens and serve with remaining ketchup glaze on the side for dipping.",strMealThumb= "asserts/o2wb6p1581005243.jpg",strTags= null,strYoutube= "",strIngredient1= "Potatoes",strIngredient2= "Carrots",strIngredient3= "Scallions",strIngredient4= "Garlic",strIngredient5= "Bread",strIngredient6= "Garlic Powder",strIngredient7= "Soy Sauce",strIngredient8= "Ground Beef",strIngredient9= "Vegetable Oil",strIngredient10= "Sugar",strIngredient11= "Butter",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "5",strMeasure2= "12 ounces",strMeasure3= "1",strMeasure4= "2 cloves",strMeasure5= "1 Slice",strMeasure6= "1",strMeasure7= "2",strMeasure8= "1",strMeasure9= "1 tsp ",strMeasure10= "2 tsp",strMeasure11= "2 tbsp",strMeasure12= " ",strMeasure13= " ",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "52815",strMeal= "French Lentils With Garlic and Thyme",strDrinkAlternate= null,strCategory= "Miscellaneous",strArea= "French",strInstructions= "Place a large saucepan over medium heat and add oil. When hot, add chopped vegetables and sauté until softened, 5 to 10 minutes.\r\nAdd 6 cups water, lentils, thyme, bay leaves and salt. Bring to a boil, then reduce to a fast simmer.\r\nSimmer lentils until they are tender and have absorbed most of the water, 20 to 25 minutes. If necessary, drain any excess water after lentils have cooked. Serve immediately, or allow them to cool and reheat later.\r\nFor a fuller taste, use some chicken stock and reduce the water by the same amount.",strMealThumb= "asserts/vwwspt1487394060.jpg",strTags= "Pulse",strYoutube= "https://www.youtube.com/watch?v=CrlTS1mJQMA",strIngredient1= "Olive Oil",strIngredient2= "Onion",strIngredient3= "Garlic",strIngredient4= "Carrot",strIngredient5= "French Lentils",strIngredient6= "Thyme",strIngredient7= "Bay Leaf",strIngredient8= "Salt",strIngredient9= "Celery",strIngredient10= "",strIngredient11= "",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "3 tablespoons",strMeasure2= "1",strMeasure3= "2 cloves",strMeasure4= "1",strMeasure5= "2 1/4 cups",strMeasure6= "1 teaspoon",strMeasure7= "3",strMeasure8= "1 tablespoon",strMeasure9= "2 sticks",strMeasure10= "",strMeasure11= "",strMeasure12= "",strMeasure13= "",strMeasure14= "",strMeasure15= "",strMeasure16= "",strMeasure17= "",strMeasure18= "",strMeasure19= "",strMeasure20= "",strSource= "",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "53008",strMeal= "Stuffed Lamb Tomatoes",strDrinkAlternate= null,strCategory= "Lamb",strArea= "Greek",strInstructions= "Heat oven to 180C/160C fan/gas 4. Slice the tops off the tomatoes and reserve. Scoop out most of the pulp with a teaspoon, being careful not to break the skin. Finely chop the pulp, and keep any juices. Sprinkle the insides of the tomatoes with a little sugar to take away the acidity, then place them on a baking tray.\r\n\r\nHeat 2 tbsp olive oil in a large frying pan, add the onion and garlic, then gently cook for about 10 mins until soft but not coloured. Add the lamb, cinnamon and tomato purée, turn up the heat, then fry until the meat is browned. Add the tomato pulp and juice, the rice and the stock. Season generously. Bring to the boil, then simmer for 15 mins or until the rice is tender and the liquid has been absorbed. Set aside to cool a little, then stir in the herbs.\r\n\r\nStuff the tomatoes up to the brim, top tomatoes with their lids, drizzle with 2 tbsp more olive oil, sprinkle 3 tbsp water into the tray, then bake for 35 mins. Serve with salad and crusty bread, hot or cold.",strMealThumb= "asserts/u55lbp1585564013.jpg",strTags= null,strYoutube= "https://www.youtube.com/watch?v=KfJp-QfrCz4",strIngredient1= "Tomatoes",strIngredient2= "Sugar",strIngredient3= "Olive Oil",strIngredient4= "Onion",strIngredient5= "Garlic Clove",strIngredient6= "Lamb",strIngredient7= "Cinnamon",strIngredient8= "Tomato Puree",strIngredient9= "Rice",strIngredient10= "Chicken Stock",strIngredient11= "Dill",strIngredient12= "Chopped Parsley",strIngredient13= "Mint",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "4 large",strMeasure2= "Pinch",strMeasure3= "4 tbs",strMeasure4= "1 chopped",strMeasure5= "2 finely chopped",strMeasure6= "200g",strMeasure7= "1 tbs",strMeasure8= "2 tbs chopped",strMeasure9= "50g",strMeasure10= "100ml",strMeasure11= "4 tbs",strMeasure12= "2 tbs",strMeasure13= "1 tbs",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "https://www.bbcgoodfood.com/recipes/stuffed-tomatoes-lamb-mince-dill-rice",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "53067",strMeal= "Stuffed Bell Peppers with Quinoa and Black Beans",strDrinkAlternate= null,strCategory= "Vegetarian",strArea= "Mexican",strInstructions= "1. Preheat your oven to 375°F (190°C). Lightly grease a 9x13-inch baking dish or a similar-sized casserole dish.\r\n2. Place the bell pepper halves in the prepared baking dish, cut side up. Bake for 15-20 minutes, or until slightly softened.\r\n3. While the bell peppers are baking, prepare the filling. In a large skillet, heat the olive oil over medium heat. Add the chopped onion, and cook for 3-4 minutes, until softened. Add the garlic, and cook for another 1 minute, until fragrant.\r\n4. Stir in the cooked quinoa, black beans, corn, diced tomatoes, ground cumin, chili powder, smoked paprika, salt, and pepper. Cook for 5-7 minutes, until heated through. Remove the skillet from heat, and stir in 1 cup of the shredded cheese, if using.\r\n5. Remove the bell peppers from the oven, and carefully stuff each pepper half with the quinoa and black bean mixture. Top the stuffed peppers with the remaining 1/2 cup of shredded cheese, if using.\r\n6. Return the stuffed peppers to the oven, and bake for another 15-20 minutes, until the cheese is melted and the peppers are tender.\r\n7. Remove from the oven, and allow the stuffed peppers to cool for 5 minutes before serving. Garnish with fresh chopped cilantro.",strMealThumb= "asserts/b66myb1683207208.jpg",strTags= null,strYoutube= "",strIngredient1= "Green Pepper",strIngredient2= "Olive Oil",strIngredient3= "Onion",strIngredient4= "Garlic",strIngredient5= "Quinoa",strIngredient6= "Black Beans",strIngredient7= "Sweetcorn",strIngredient8= "Diced Tomatoes",strIngredient9= "Cumin",strIngredient10= "Chili Powder",strIngredient11= "Smoked Paprika",strIngredient12= "Salt",strIngredient13= "Pepper",strIngredient14= "Shredded Mexican Cheese",strIngredient15= "Cilantro",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "4 whole",strMeasure2= "1 tablespoon",strMeasure3= "1 small finely diced",strMeasure4= "2 cloves minced",strMeasure5= "1 cups",strMeasure6= "1 can ",strMeasure7= "1 cup ",strMeasure8= "1 can ",strMeasure9= "1 teaspoon",strMeasure10= "½ tsp",strMeasure11= "½ tsp",strMeasure12= "To taste",strMeasure13= "To taste",strMeasure14= "1 1/2 cup ",strMeasure15= "Chopped",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "52949",strMeal= "Sweet and Sour Pork",strDrinkAlternate= null,strCategory= "Pork",strArea= "Chinese",strInstructions= "Preparation\r\n1. Crack the egg into a bowl. Separate the egg white and yolk.\r\n\r\nSweet and Sour Pork\r\n2. Slice the pork tenderloin into strips.\r\n\r\n3. Prepare the marinade using a pinch of salt, one teaspoon of starch, two teaspoons of light soy sauce, and an egg white.\r\n\r\n4. Marinade the pork strips for about 20 minutes.\r\n\r\n5. Put the remaining starch in a bowl. Add some water and vinegar to make a starchy sauce.\r\n\r\nSweet and Sour Pork\r\nCooking Instructions\r\n1. Pour the cooking oil into a wok and heat to 190°C (375°F). Add the marinated pork strips and fry them until they turn brown. Remove the cooked pork from the wok and place on a plate.\r\n\r\n2. Leave some oil in the wok. Put the tomato sauce and white sugar into the wok, and heat until the oil and sauce are fully combined.\r\n\r\n3. Add some water to the wok and thoroughly heat the sweet and sour sauce before adding the pork strips to it.\r\n\r\n4. Pour in the starchy sauce. Stir-fry all the ingredients until the pork and sauce are thoroughly mixed together.\r\n\r\n5. Serve on a plate and add some coriander for decoration.",strMealThumb= "asserts/1529442316.jpg",strTags= "Sweet",strYoutube= "https://www.youtube.com/watch?v=mdaBIhgEAMo",strIngredient1= "Pork",strIngredient2= "Egg",strIngredient3= "Water",strIngredient4= "Salt",strIngredient5= "Sugar",strIngredient6= "Soy Sauce",strIngredient7= "Starch",strIngredient8= "Tomato Puree",strIngredient9= "Vinegar",strIngredient10= "Coriander",strIngredient11= "",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "200g",strMeasure2= "1",strMeasure3= "Dash",strMeasure4= "1/2 tsp",strMeasure5= "1 tsp ",strMeasure6= "10g",strMeasure7= "10g",strMeasure8= "30g",strMeasure9= "10g",strMeasure10= "Dash",strMeasure11= "",strMeasure12= "",strMeasure13= "",strMeasure14= "",strMeasure15= "",strMeasure16= "",strMeasure17= "",strMeasure18= "",strMeasure19= "",strMeasure20= "",strSource= "https://www.chinahighlights.com/travelguide/chinese-food/cooking/sweet-sour-pork.htm",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "52782",strMeal= "Lamb tomato and sweet spices",strDrinkAlternate= null,strCategory= "Lamb",strArea= "Moroccan",strInstructions= "Use pickled vine leaves here, preserved in brine. Small delicate leaves are better than the large bristly ones but, if only large leaves are to hand, then trim them to roughly 12 by 12 cms so that you don't get too many layers of leaves around the filling. And remove any stalks. Drain the preserved leaves, immerse them in boiling water for 10 minutes and then leave to dry on a tea towel before use. \r\nBasmati rice with butter and pine nuts is an ideal accompaniment. Couscous is great, too. Serves four.\r\nFirst make the filling. Put all the ingredients, apart from the tomatoes, in a bowl. Cut the tomatoes in half, coarsely grate into the bowl and discard the skins. Add half a teaspoon of salt and some black pepper, and stir. Leave on the side, or in the fridge, for up to a day. Before using, gently squeeze with your hands and drain away any juices that come out.\r\nTo make the sauce, heat the oil in a medium pan. Add the ginger and garlic, cook for a minute or two, taking care not to burn them, then add the tomato, lemon juice and sugar. Season, and simmer for 20 minutes.\r\nWhile the sauce is bubbling away, prepare the vine leaves. Use any torn or broken leaves to line the base of a wide, heavy saucepan. Trim any leaves from the fennel, cut it vertically into 0.5cm-thick slices and spread over the base of the pan to cover completely.\r\nLay a prepared vine leaf (see intro) on a work surface, veiny side up. Put two teaspoons of filling at the base of the leaf in a 2cm-long by 1cm-wide strip. Fold the sides of the leaf over the filling, then roll it tightly from bottom to top, in a cigar shape. Place in the pan, seam down, and repeat with the remaining leaves, placing them tightly next to each other in lines or circles (in two layers if necessary).\r\nPour the sauce over the leaves (and, if needed, add water just to cover). Place a plate on top, to weigh the leaves down, then cover with a lid. Bring to a boil, reduce the heat and cook on a bare simmer for 70 minutes. Most of the liquid should evaporate. Remove from the heat, and leave to cool a little - they are best served warm. When serving, bring to the table in the pan - it looks great. Serve a few vine leaves and fennel slices with warm rice. Spoon the braising juices on top and garnish with coriander.",strMealThumb= "asserts/qtwtss1468572261.jpg",strTags= "",strYoutube= "https://www.youtube.com/watch?v=vaZb1MnFBgA",strIngredient1= "olive oil",strIngredient2= "ginger",strIngredient3= "garlic",strIngredient4= "tomatoes",strIngredient5= "lemon juice",strIngredient6= "caster sugar",strIngredient7= "vine leaves",strIngredient8= "fennel bulb",strIngredient9= "lamb mince",strIngredient10= "onion",strIngredient11= "potato",strIngredient12= "basmati rice",strIngredient13= "chopped parsley",strIngredient14= "coriander",strIngredient15= "lemon juice",strIngredient16= "garlic",strIngredient17= "clove",strIngredient18= "cinnamon",strIngredient19= "tomatoes",strIngredient20= "",strMeasure1= "2 tbsp",strMeasure2= "4cm piece finely chopped",strMeasure3= "2 cloves peeled and chopped",strMeasure4= "800g peeled and chopped ",strMeasure5= "2 tbsp",strMeasure6= "1 tsp",strMeasure7= "50",strMeasure8= "1 large",strMeasure9= "400g",strMeasure10= "1 medium",strMeasure11= "1 small peeled and coarsely grated",strMeasure12= "2 tbsp",strMeasure13= "2 tbsp",strMeasure14= "2 tbsp chopped",strMeasure15= "1 tbsp",strMeasure16= "2 cloves",strMeasure17= "½ tsp ground",strMeasure18= "½ tsp ground ",strMeasure19= "2 medium",strMeasure20= "",strSource= "http://www.ottolenghi.co.uk/recipes/meat/lamb-tomato-and-sweet-spices-shop",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
                new Receipe() { idMeal= "52994",strMeal= "Skillet Apple Pork Chops with Roasted Sweet Potatoes & Zucchini",strDrinkAlternate= null,strCategory= "Pork",strArea= "American",strInstructions= "\r\nServes 2\r\n\r\n\r\n1. \r\n\r\nAdjust racks to top and middle positions and preheat oven to 450 degrees. Wash and dry all produce. Dice sweet potatoes into 1/2-inch pieces. Toss on a baking sheet with a drizzle of oil, salt, and pepper. Roast on top rack for 12 minutes (we'll roast the zucchini then). \r\n\r\n\r\n2. \r\n\r\nMeanwhile, halve and core apple; thinly slice into half-moons. Peel and finely chop garlic. Quarter lemon. Trim and halve zucchini lengthwise; cut crosswise into 1/2-inch-thick half-moons. Toss on a second baking sheet with a drizzle of oil and a pinch of salt and pepper. Set aside. \r\n\r\n\r\n3. \r\n\r\nPat pork dry with paper towels and season all over with salt and pepper. Heat a drizzle of oil in a large pan over medium-high heat. Add pork and cook until browned and cooked through, 4-5 minutes per side. Turn off heat; transfer to a plate. \r\n\r\n\r\n4. \r\n\r\nOnce sweet potatoes have roasted 12 minutes, transfer baking sheet with zucchini to middle rack and continue roasting until both veggies are browned and softened, 12-15 minutes more. \r\n\r\n\r\n5. \r\n\r\nMeanwhile, melt 1 TBSP butter (2 TBSP for 4 servings) in pan used for pork over medium-high heat. Add apple and season with salt and pepper. Cook, scraping up any browned bits from bottom of pan, until apple is slightly softened, 2-3 minutes. Add garlic; cook until fragrant, 30 seconds. Add 1/z cup water (3/4 cup for 4), stock concentrate, and 11/2 tsp sugar (3 tsp for 4). Cook, stirring, until sauce has thickened and apple is very tender, 3-5 minutes. Season with salt and pepper. \r\n\r\n\r\n6. \r\n\r\nRemove pan with apple from heat; stir in 1 TBSP butter (2 TBSP for 4 servings) and a squeeze of lemon juice. Divide pork, zucchini, and sweet potatoes between plates. Top pork with glazed apple sauce. Top zucchini with a squeeze of lemon juice. ",strMealThumb= "asserts/h3ijwo1581013377.jpg",strTags= null,strYoutube= "",strIngredient1= "Potatoes",strIngredient2= "Apples",strIngredient3= "Garlic",strIngredient4= "Lemon",strIngredient5= "Pork",strIngredient6= "Zucchini",strIngredient7= "Chicken Stock",strIngredient8= "Vegetable Oil",strIngredient9= "Sugar",strIngredient10= "Butter",strIngredient11= "",strIngredient12= "",strIngredient13= "",strIngredient14= "",strIngredient15= "",strIngredient16= "",strIngredient17= "",strIngredient18= "",strIngredient19= "",strIngredient20= "",strMeasure1= "2",strMeasure2= "1",strMeasure3= "2 cloves",strMeasure4= "1",strMeasure5= "2",strMeasure6= "1",strMeasure7= "1",strMeasure8= "1 tbsp",strMeasure9= "1 1/2 tsp ",strMeasure10= "2 tbsp",strMeasure11= " ",strMeasure12= " ",strMeasure13= " ",strMeasure14= " ",strMeasure15= " ",strMeasure16= " ",strMeasure17= " ",strMeasure18= " ",strMeasure19= " ",strMeasure20= " ",strSource= "",strImageSource= null,strCreativeCommonsConfirmed= null,dateModified= null },
